import React, { Component } from "react";
import axios from "axios";
import { Badge, Card ,Col,Image} from "react-bootstrap";

export default class Dashbord extends Component {
  constructor(props) {
    super(props);
    this.state = {
      res: {
        update_date_time: "",
        local_new_cases: " ",
        local_total_cases: " ",
        local_total_number_of_individuals_in_hospitals: "",
        local_deaths: "",
        local_new_deaths: "",
        local_recovered: "",
        local_active_cases: "",
        global_new_cases: "",
        global_total_cases: "",
        global_deaths: "",
        global_new_deaths: "",
        global_recovered: "",
        hospital_data: [],
      },
    };
  }

  componentDidMount() {
    axios
      .get("https://www.hpb.health.gov.lk/api/get-current-statistical")
      .then((response) => {
        console.log(response.data.data);
        this.setState({ res: response.data.data });
      })
      .catch(function (response) {});
  }

  render() {
    return (
      <div>
        <h1>
          <Badge variant="secondary">Covid-19 Sri Lanka </Badge>
        </h1>
        <Card>
        <Col xs={6} md={4}>
      <Image src="holder.js/171x180" rounded />
    </Col>
          <Card.Body>
            <Card.Title>Card title</Card.Title>
          </Card.Body>
          <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
          </Card.Footer>
        </Card>
        <h1>Upade Time :{this.state.res.update_date_time}</h1>
        <h1>Total Case: {this.state.res.local_total_cases}</h1>
        <h1>Deaths :{this.state.res.local_deaths}</h1>
        <h1>Total Recovered:{this.state.res.local_recovered}</h1>
      </div>
    );
  }
}
