import React, { Component } from "react";
import axios from "axios";

export default class Dashbord extends Component {
  constructor(props) {
    super(props);
    this.state = {
        res:{
            update_date_time: '',
            local_new_cases: ' ',
            local_total_cases: ' ',
            local_total_number_of_individuals_in_hospitals: '',
            local_deaths:'',
            local_new_deaths:'',
            local_recovered:'',
            local_active_cases:'',
            global_new_cases: '',
            global_total_cases: '',
            global_deaths: '',
            global_new_deaths: '',
            global_recovered:'',
            hospital_data:[],
        }
    };
  }

  componentDidMount() {
    axios
      .get("https://www.hpb.health.gov.lk/api/get-current-statistical")
      .then((response) => {
        console.log(response.data.data);
        this.setState({ res: response.data.data });
      })
      .catch(function (response) {
       
      });
  }

  render() {
    return (
      <div>
          <h1>Upade time :{this.state.res.update_date_time}</h1>
          <h1>Totel {this.state.res.local_total_cases}</h1>
          <h1>{this.state.res.local_deaths}</h1>
      </div>
    );
  }
}
